<script setup lang="ts">
import { ref } from 'vue'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

const props = defineProps<{
  source: string
  alt: string
  className: string
}>()

const didError = ref(false)

const handleError = () => {
  didError.value = true
}
</script>

<template>
  <div
    v-if="didError"
    :class="['inline-block bg-gray-100 text-center align-middle', props.className]"
  >
    <div class="flex items-center justify-center w-full h-full">
      <img :src="ERROR_IMG_SRC" alt="Error loading image" :data-original-url="props.source" />
    </div>
  </div>

  <img v-else :src="props.source" :alt="props.alt" :class="props.className" @error="handleError" />
</template>
